def ApproxPatternMatch(Pattern,Text,d):
	k=len(Pattern)
	StartingPositions=[]
	for i in range(len(Text)):
		if find_Ham(Pattern,Text[i:i+k])<= d and len(Pattern)==len(Text[i:i+k]):
			StartingPositions.append(i)
	return StartingPositions

